function showMe(e) {
    console.log(e.src);
    var modalImage = document.getElementById('modPic');
    modalImage.src = e.src
}

function zoomin() {
    var FST = document.getElementById("modPic");
    var currWidth = FST.clientWidth;
    FST.style.width = (currWidth + 100) + "px";
}

function zoomout() {
    var FST = document.getElementById("modPic");
    var currWidth = FST.clientWidth;
    FST.style.width = (currWidth - 100) + "px";
}


